    const [isSOpen, setIsSOpen] = useState(false)
        const [isSidebarOpen, setIsSidebarOpen] = useState(false)


    const openModal = ()=> {
        setIsModalOpen(true); 
    }
    const closeModal = ()=> {
        setIsModalOpen(false)
    ; 
}
    const openSidebar = ()=> {
        setIsSidebarOpen(true); 
}
    const closeSidebar = ()=> {
        setIsSidebarOpen(false); 
}


    return<>
    <AppContext.Provider value={{isModalOpen, openModal,closeModal, isSidebarOpen, closeSidebar, openSidebar}}> {children}
    </AppContext.Provider>
    </>
}

export const useGlobalContext = () => {
return useContext(AppContext)
}

